import { Injectable } from '@angular/core';
import { combineLatest, map, Observable, of } from 'rxjs';
import { IMenuItem } from '../libs/models/menu-item';
import rboData from '../static/rbo.static-file.json';
import rbbData from '../static/rbb.static-file.json';

@Injectable()
export class DataService {
  constructor() {}

  getData(type: string) {
    return combineLatest([this.getStaticData(type), this.getTridionData(type)]).pipe(
      map(([staticData, tridionData]) => {
        return [...staticData, ...tridionData];
      })
    );
  }

  getStaticData(type: string): Observable<IMenuItem[]> {

    let dataSource = null;
    if('rbo' === type) {
      dataSource = rboData as IMenuItem[];
    }
    if('rbb' === type) {
      dataSource = rbbData as IMenuItem[];
    }
    return of(dataSource);
  }

  getTridionData(type: string): Observable<IMenuItem[]> {
    const dataSource = data as IMenuItem[];
    return of(dataSource);
  }
}
